INSERT INTO `book` VALUES (1521453234858207347, 1521453234854567888, '你所谓的稳定，不过是在浪费生命（当当专享签名版！李尚龙励志代表作）', '李尚龙', '北京联合出版有限公司', 35.8, '//img3m5.ddimg.cn/76/19/29382385-1_w_2.jpg', '百万销量作者李尚龙励志代表作。80万册销量认证，新增2万字原创内容，全网上亿人热议话题，引发百万年轻人反思生活的真实之书！别在该拼搏的年纪，选择躺平！', '2022-05-07 10:36:29', '2022-05-07 10:36:29', 1, 0);
