INSERT INTO `book` VALUES (1521453234861756534, 1521453234854564569, '蹦极（2021年度中国好书获奖图书，外交官的炼成之路）', '卢山', '江苏凤凰文艺出版社', 37.1, '//img3m8.ddimg.cn/71/33/29259818-1_w_16.jpg', '[赵叔叔同款] 书写外交官这群无名英雄的风骨，揭示中国崛起之不易。看似光鲜的外交官，背负的是孤独、隐忍、清贫，坚守的是信仰', '2022-05-07 10:34:08', '2022-05-07 10:34:08', 1, 0);
