INSERT INTO `book` VALUES (1521453234882061888, 1521453234854567888, '有梗', '黄西', '天津科学技术出版社', 47.7, '//img3m9.ddimg.cn/85/9/28995799-1_w_30.jpg', '全美脱口秀冠军黄西的幽默沟通课快速提高幽默表达。黄西20年幽默生涯精华之作，4个演说工具12个沟通方法和17个幽默技巧，幽默感可以后天养成。后疫情时代，让幽默成为打败焦虑、悲观等不良情绪的武器', '2022-05-07 10:36:32', '2022-05-07 10:36:32', 1, 0);
